import React, {useState, useEffect} from "react";
import { View, Text, TextInput, StyleSheet } from "react-native";
import CustomButton from "../CustomButton"
import { DataTable } from "react-native-paper";

const optionsPerPage = [2, 3, 4];

const CustomTable = ({ budgets, idCategorie}) => {

  console.log(budgets)
  // console.log(idCategorie)
  const [page, setPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(optionsPerPage[0]);

  useEffect(() => {
    setPage(0);
  }, [itemsPerPage]);


  return (
    <DataTable>
      <DataTable.Header>
        <DataTable.Title>Suppr</DataTable.Title>
        {/* <DataTable.Title>Date</DataTable.Title> */}
        <DataTable.Title>Achat</DataTable.Title>

        <DataTable.Title>Montant</DataTable.Title>

        <DataTable.Title>Débité</DataTable.Title>
      </DataTable.Header>

        {budgets.map((budget) => {
          if(budget.field.field_category_id == idCategorie){
            // console.log(budget)
            return (
              <>
                <DataTable.Row key={budget.id}>
                  <DataTable.Cell key={budget.id + " delete"}>
                    <CustomButton
                      onPress={() => {
                        console.warn("pouet");
                      }}
                      text={"X"}
                    // type={"TERTIARY"}
                    />
                  </DataTable.Cell>
                  {/* <DataTable.Cell key={budget.id + budget.created_at}>
                  Date
                </DataTable.Cell> */}
                  <DataTable.Cell key={budget.id + budget.field_id}>
                    <CustomButton
                      onPress={() => {
                        console.warn("achat");
                      }}
                      text={budget.field.name}
                      type={"SECONDARY"}
                    />
                  </DataTable.Cell>
                  <DataTable.Cell key={budget.id + budget.value}>
                    <CustomButton
                      onPress={() => {
                        console.warn("montant");
                      }}
                      text={budget.value}
                      type={"SECONDARY"}
                    />
                  </DataTable.Cell>
                  <DataTable.Cell key={budget.id + budget.is_debited}>
                    <CustomButton
                      onPress={() => {
                        console.warn("débité");
                      }}
                      text={budget.is_debited}
                      type={"SECONDARY"}
                    />
                  </DataTable.Cell>
                </DataTable.Row>
              </>
            );
          }


        })}


      <DataTable.Pagination
        page={page}
        numberOfPages={3}
        onPageChange={(page) => setPage(page)}
        label="1-2 of 6"
        optionsPerPage={optionsPerPage}
        itemsPerPage={itemsPerPage}
        setItemsPerPage={setItemsPerPage}
        showFastPagination
        optionsLabel={"Rows per page"}
      />
    </DataTable>
  );
};
export default CustomTable;

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    color: "white",
    width: "100%",
    borderColor: "salmon",
    borderWidth: 1,
    borderRadius: 5,
    padding: 10,
    marginVertical: 5,
  },

  container_PRIMARY: {
    backgroundColor: "salmon",
  },
  container_SECONDARY: {
    borderColor: "salmon",
    borderWidth: 2,
  },

  container_TERTIARY: {
    borderWidth: 0,
  },
  container_SOCIALCONNECT: {
    borderWidth: 0,
  },

  text: {
    fontWeight: "bold",
    color: "white",
    textTransform: "uppercase",
  },
  text_SECONDARY: {
    color: "salmon",
  },

  text_TERTIARY: {
    fontWeight: "300",
    color: "#e5e7e9",
    textTransform: "none",
    fontStyle: "italic",
  },
});
