import React from 'react'
import { StyleSheet, Text, View, Alert } from 'react-native'
import { useNavigation } from "@react-navigation/native";

const navigation = useNavigation();
const CustomAlert = (title = "Titre", message = "Message d'alerte", button1 = "Ok", fnButton1 = console.warn(fnButton1), button2= "Cancel", fnButton2 = console.warn(fnButton2)) => {
  const noMatch = () =>
    Alert.alert(title, message, [
      {
        text: button1,
        onPress: () => navigation.navigate(fnButton1),
      },
      {
        text: button2,
        onPress: () => fnButton2,
        style: "cancel",
      },
    ]);
  return (
    noMatch()
  )
}

export default CustomAlert

const styles = StyleSheet.create({})
