import React, { useState } from 'react'
import { View, Text, StyleSheet, ScrollView } from 'react-native'
import CustomInput from '../../components/CustomInput'
import CustomButton from '../../components/CustomButton'
import SocialSignInButtons from '../../components/SocialSignInButtons'

const confirmEmailScreen = () => {
    const [email, setEmail] = useState("");
    const [confirmCode, setConfirmCode] = useState("");

    const onConfirmPress = () => {
        console.warn("Confirmer l'email");
    }

    const onAlreadyRegisterPress = () => {
      console.warn("Déjà inscrit ? Se connecter");
    };

    const OnPressUsePolicy = () => {
      console.warn("Use Policy");
    }

    const OnPressPrivacyPolicy = () => {
      console.warn("Privacy Policy");
    };

    return (
      <ScrollView>
        <View style={styles.root}>
          <Text style={styles.title}>Confirmer votre email</Text>

          <CustomInput
            placeholder={"Entrez le code de confirmation"}
            value={confirmCode}
            setValue={setConfirmCode}
            secureTextEntry={true}
          />

          <CustomButton onPress={onConfirmPress} text={"Confirmer mon email"} />

          <Text style={styles.text}>
            En vous enregistrant, vous acceptez la{" "}
            <Text style={styles.link} onPress={OnPressUsePolicy}>
              politique d'utilisation
            </Text>{" "}
            ainsi que la{" "}
            <Text style={styles.link} onPress={OnPressPrivacyPolicy}>
              politique de confidentialité
            </Text>{" "}
            d'OrgaBud
          </Text>

          <SocialSignInButtons />
          <CustomButton
            onPress={onAlreadyRegisterPress}
            text={"Déjà inscrit ? Connectez-vous"}
            type="TERTIARY"
          />
        </View>
      </ScrollView>
    );
}

export default confirmEmailScreen;

const styles = StyleSheet.create({
  root: {
    padding: 10,
    alignItems: "center",
  },
  title: {
    textTransform: "capitalize",
    fontSize: 24,
    fontWeight: "bold",
    margin: 10,
  },
  text:{
    color: "#fff",
    marginVertical: 10
  },
  link:{
    color: "salmon"
  }
});
