import React, { useState } from 'react'
import { View, Text, StyleSheet, ScrollView } from 'react-native'
import CustomInput from '../../components/CustomInput'
import CustomButton from '../../components/CustomButton'
import SocialSignInButtons from '../../components/SocialSignInButtons'

const confirmEmailScreen = () => {
    const [email, setEmail] = useState("");
    const [confirmCode, setConfirmCode] = useState("");

    const onConfirmPress = () => {
        console.warn("Confirmer l'email");
    }

    const backToSignIn = () => {
      console.warn("Retour à la connexion");
    };

    const Resend = () => {
      console.warn("Renvoyer l'email");
    };


    return (
      <ScrollView>
        <View style={styles.root}>
          <Text style={styles.title}>Confirmer votre email</Text>

          <CustomInput
            placeholder={"Email"}
            value={email}
            setValue={setEmail}
          />
          <CustomInput
            placeholder={"Entrez le code de confirmation"}
            value={confirmCode}
            setValue={setConfirmCode}
            secureTextEntry={true}
          />

          <CustomButton onPress={onConfirmPress} text={"Confirmer mon email"} />

          <CustomButton
            onPress={backToSignIn}
            text={"Retour à la connexion"}
            type="TERTIARY"
          />

          <CustomButton
            onPress={Resend}
            text={"Renvoyer l'email"}
            type="TERTIARY"
          />
        </View>
      </ScrollView>
    );
}

export default confirmEmailScreen;

const styles = StyleSheet.create({
  root: {
    padding: 10,
    alignItems: "center",
  },
  title: {
    textTransform: "capitalize",
    fontSize: 24,
    fontWeight: "bold",
    margin: 10,
  },
  text:{
    color: "#fff",
    marginVertical: 10
  },
  link:{
    color: "salmon"
  }
});
