export const login = (email, password) => {
    axios({
        method: "post",
        url: URI,
        data: {
            email: email,
            password: password,
        },
        }).then((response) => {
        //Si la response à la requête est acceptée par l'API, l'utilisateur est redirigé vers la page d'accueil.
        // console.warn(state)
        response.status == 201
            ? saveToken(
                response.data.token
            ) /* navigation.navigate('Home', {
                    token : response.data.token,
                })  */
            : {};
    });
}
