import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View } from 'react-native'
import CustomButton from "../../components/CustomButton";
import { useNavigation } from "@react-navigation/native";
import { API } from "../../services/env";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";




const HomeScreen = () => {
  const [token, setToken] = useState('');
  const [dataCategories, setDataCategories] = useState([]);
  // const [categories, setCategories] = useState([]);
  const getToken= async() => {
    try {
      const token = await AsyncStorage.getItem('token')
      setToken(token)
    } catch (error) {
      console.warn(error)
    }
  }
  // console.log(token)
  const navigation = useNavigation();
  const URI = API + "categories"
  const goBackSignPress = () => {
    navigation.navigate("SignIn");
  };
  const categorieOnPress = (name, slug) => {
    // console.warn(name, slug)
    navigation.navigate("Categorie", {
      categorieName: name, slug,
    });
  }
  const getCategories = async() => {

    const response = await axios({
      method: "get",
      url: URI,
      headers: {
        Authorization: `Bearer ${token}`,
      },
    }).catch(function (error) {
      console.log(error);
    }).then(function(response){
      // console.log(response.data[0].name)
      setDataCategories(response.data)
    });

  }

  getToken()
  useEffect(() => {
    //  getCategories()
  }, [])

    return (
      <View style={styles.root}>
        <Text style={{ fontSize: 24, alignContent: "center" }}>
          Accueil de l'utilisateur Authentifié
        </Text>
        <CustomButton
          onPress={goBackSignPress}
          text={"Retourner à l'authentification "}
        />
        <Text style={{ fontSize: 20, alignContent: "center"}}>
          Catégories
        </Text>

        {dataCategories.map((categorie) => {
          return (
            <CustomButton key={categorie.slug.toString()}onPress={() => categorieOnPress(categorie.name,categorie.slug)} text={categorie.name} />
          );
        })}

      </View>
    );
}

export default HomeScreen

const styles = StyleSheet.create({
  root: {
    padding: 10,
    backgroundColor: "#f5b7b1",
  },
});
