import {useState} from 'react'
import { API } from "./env"
import axios from "axios";

export const [CATEGORIE, SETCATEGORIE] = useState('')
export const CategorieDetail = async (arg) => {
    const URI = API + arg
    var retour = ''
    const response = await axios({
    method: "get",
    url: URI,
      //   headers: {
      //     Authorization: `Bearer ${token}`,
      //   },
    }).then(function (response) {
    SETCATEGORIE(response.data)
    });
}
