import React, { useState } from 'react'
import { View, Text, Image, StyleSheet, useWindowDimensions } from 'react-native'
import Logo from '../../../assets/images/orgabudLogo.png'
import CustomInput from '../../components/CustomInput/CustomInput'
import CustomButton from '../../components/CustomButton/CustomButton'

const SignInScreen = () => {
    const [username, setUsername]= useState('');
    const [password, setPassword] = useState('');
    const {height} = useWindowDimensions();
    const onSignInPress = () => {
        console.warn('Connecté');
    }
    return (
        <View style={styles.root}>
            <Image source={Logo} style={[styles.logo, {height: height * 0.3}]}  resizeMode="contain" />
            <CustomInput placeholder={"Email"} value={username} setValue={setUsername} secureTextEntry={false} />
            <CustomInput placeholder={"Mot de Passe"} value={password} setValue={setPassword} secureTextEntry={true} />
            <CustomButton onPress={ onSignInPress } text={"Se connecter"}/>
        </View>
    )
}

export default SignInScreen

const styles = StyleSheet.create({
    root:{
        padding: 10
    },
    logo: {
        width : '60%',
        maxWidth: 200,
        height: '60%',
        maxHeight : 200,
        marginBottom: 50,
    },
});
