import React, { useState } from 'react'
import { View, Text, StyleSheet, ScrollView } from 'react-native'
import CustomInput from '../../components/CustomInput'
import CustomButton from '../../components/CustomButton'

const ForgotPassWordScreen = () => {
  const [email, setEmail] = useState("");
  const [confirmCode, setConfirmCode] = useState("");

  const onResetPassword = () => {
    console.warn("Renvoyer le mot de passe");
  };

  const onBackToSignInPress = () => {
    console.warn("Retour à la connexion");
  };

  const onResendPress = () => {
    console.warn("Renvoyer l'email");
  };

  return (
    <ScrollView>
      <View style={styles.root}>
        <Text style={styles.title}>Renvoyer le Mot de Passe</Text>

        <CustomInput 
          placeholder={"Email"} 
          value={email} 
          setValue={setEmail} 
          />

        <CustomButton onPress={onResetPassword} text={"Confirmer mon email"} />

        <CustomButton
          onPress={onResendPress}
          text={"Renvoyer l'email"}
          type="SECONDARY"
        />
        <CustomButton
          onPress={onBackToSignInPress}
          text={"Retour à la connexion"}
          type="TERTIARY"
        />
      </View>
    </ScrollView>
  );
};

export default ForgotPassWordScreen;

const styles = StyleSheet.create({
  root: {
    padding: 10,
    alignItems: "center",
  },
  title: {
    textTransform: "uppercase",
    fontSize: 24,
    fontWeight: "bold",
    margin: 10,
  },
  text:{
    color: "#fff",
    marginVertical: 10
  },
  link:{
    color: "salmon"
  }
});
