import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View } from 'react-native'
import CustomButton from "../../components/CustomButton";
import { useNavigation } from "@react-navigation/native";
import { API } from "../../services/env";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";




const HomeScreen = () => {
  const [token, setToken] = useState('')
  const [categories, setCategories] = useState([])
  const getToken= async() => {
    try {
      const token = await AsyncStorage.getItem('token')
      setToken(token)
    } catch (error) {
      console.warn(error)
    }
  }
  const navigation = useNavigation();
  const URI = API + "categories"
  const goBackSignPress = () => {
    navigation.navigate("SignIn");
  };
  useEffect(() =>{
    axios({
      method: "get",
      url: URI,
      headers: {
        Authorization: `Bearer ${token}`,
      },
    }).then((response) => {
      //Si la response à la requête est acceptée par l'API, l'utilisateur est redirigé vers la page d'accueil.
      setCategories(response.data)
      console.warn(response.data[1].name);
      // response.status == 201 ? console.warn("pouet") : console.warn("nope");
    });
  } )



  getToken()

    return (
      <View style={styles.root}>
        <Text style={{ fontSize: 24, alignContent: "center" }}>
          Accueil de l'utilisateur Authentifié
        </Text>
        <CustomButton
          onPress={goBackSignPress}
          text={"Retourner à l'authentification "}
        />
        <CustomButton
          onPress={()=> {
            console.warn('pouet')
          }}
          text={"Catégorie"}
        />
        {/* {categories.map((event) => console.warn(event))} */}
      </View>
    );
}

export default HomeScreen

const styles = StyleSheet.create({
  root: {
    padding: 10,
    backgroundColor: "#f5b7b1",
  },
});
