import React, { useState } from 'react'
import { View, Text, Image, StyleSheet, useWindowDimensions } from 'react-native'
import Logo from '../../../assets/images/orgabudLogo.png'
import CustomInput from '../../components/CustomInput/CustomInput'
import CustomButton from '../../components/CustomButton/CustomButton'

const SignInScreen = () => {
    const [username, setUsername]= useState('');
    const [password, setPassword] = useState('');
    const {height} = useWindowDimensions();
    const onSignInPress = () => {
        console.warn('Connecté');
    }

    const onSignInWithFacebookPress = () => {
      console.warn("Connecté avec Facebook");
    };

    const onSignInWithGooglePress = () => {
      console.warn("Connecté avec Google");
    };

    const onSignInWithApplePress = () => {
      console.warn("Connecté avec Apple");
    };

    const onForgotPassPress = () => {
      console.warn("Forgot");
    };

    return (
      <View style={styles.root}>
        <Image
          source={Logo}
          style={[styles.logo, { height: height * 0.3 }]}
          resizeMode="contain"
        />
        <CustomInput
          placeholder={"Email"}
          value={username}
          setValue={setUsername}
        />
        <CustomInput
          placeholder={"Mot de Passe"}
          value={password}
          setValue={setPassword}
          secureTextEntry={true}
        />
        <CustomButton onPress={onSignInPress} text={"Se connecter"} />

        <CustomButton
          onPress={onForgotPassPress}
          text={"Mot de passe oublié ?"}
          type="TERTIARY"
        />

        <CustomButton
          onPress={onSignInWithFacebookPress}
          text={"Se connecter avec Facebook"}
          bgColor="#E7EAF4"
          fgColor="#4765A9"
          type="SOCIALCONNECT"
        />
        <CustomButton
          onPress={onSignInWithGooglePress}
          text={"Se connecter avec Google"}
          bgColor="#FAE9EA"
          fgColor="#DD4D44"
          type="SOCIALCONNECT"
        />
        <CustomButton
          onPress={onSignInWithApplePress}
          text={"Se connecter avec Apple"}
          bgColor="#E3E3E3"
          fgColor="#4765A9"
          type="SOCIALCONNECT"
        />
      </View>
    );
}

export default SignInScreen

const styles = StyleSheet.create({
    root:{
        padding: 10
    },
    logo: {
        width : '60%',
        maxWidth: 200,
        height: '60%',
        maxHeight : 200,
        marginBottom: 50,
    },
});
