import React, { useState } from 'react'
import { View, Text, StyleSheet, ScrollView } from 'react-native'
import CustomInput from '../../components/CustomInput'
import CustomButton from '../../components/CustomButton'
import { useNavigation } from "@react-navigation/native";
import AwesomeAlert from "react-native-awesome-alerts";
import axios from "axios";

const URI = "http://192.168.140.73:8000/api/isRegister";

const ForgotPassWordScreen = () => {
  const navigation = useNavigation();
  const [email, setEmail] = useState("");
  const [myAlert, setMyAlert] = useState('')
  const showAlert = () => {
    return true
  }
  const hideAlert = () =>{
    return false
  }

  const onResetPassword = () => {
    //Verification en bdd si l'email est existant en base de donnée.
    axios({
      method: "post",
      url: URI,
      data: {
        email: email,
      },
    })
      .then((response) => {
        //Si l'email est en BDD je stocke le code dans la sessionStorage et j'envoie l'utilisateur vers la page de validation. sinon je renvois un message //TODOle placer en bdd plutot que dans la session et envoyer un mail à l'utilisateur contenant le code.
        response.data.Resultat
          ? sessionStorage.setItem(
              "codeReset",
              JSON.stringify(response.data.Resultat)
            )
          : {};
        response.data.Resultat ? (
          navigation.navigate("NewPassWord")
        ) : (
          <AwesomeAlert
            show={showAlert}
            showProgress={false}
            title="AwesomeAlert"
            message="I have a message for you!"
            closeOnTouchOutside={true}
            closeOnHardwareBackPress={false}
            showCancelButton={true}
            showConfirmButton={true}
            cancelText="No, cancel"
            confirmText="Yes, delete it"
            confirmButtonColor="#DD6B55"
            onCancelPressed={() => {
              this.hideAlert();
            }}
            onConfirmPressed={() => {
              this.hideAlert();
            }}
          />
        );/* Alert.alert("non"); */
      })
      .catch((error) => console.log(error));

  };

  const onBackToSignInPress = () => {
    navigation.navigate("SignIn");
  }

  return (
    <ScrollView>
      <View style={styles.root}>
        <Text style={styles.title}>Renvoyer le Mot de Passe</Text>

        <CustomInput 
          placeholder={"Email"} 
          value={email} 
          setValue={setEmail} 
          />

        <CustomButton onPress={onResetPassword} text={"Réinitialiser le mot de passe"} />

        <CustomButton
          onPress={onBackToSignInPress}
          text={"Retour à la connexion"}
          type="TERTIARY"
        />
      </View>
    </ScrollView>
  );
};

export default ForgotPassWordScreen;

const styles = StyleSheet.create({
  root: {
    padding: 10,
    alignItems: "center",
    backgroundColor: "#f5b7b1",
  },
  title: {
    textTransform: "uppercase",
    fontSize: 24,
    fontWeight: "bold",
    margin: 10,
  },
  text: {
    color: "#fff",
    marginVertical: 10,
  },
  link: {
    color: "salmon",
  },
});
