import React, { useState } from 'react'
import { View, Text, Image, StyleSheet, useWindowDimensions, ScrollView } from 'react-native'
import Logo from "../../../assets/images/orgabud_with_title.png";
import CustomInput from '../../components/CustomInput/CustomInput'
import CustomButton from '../../components/CustomButton/CustomButton'

const SignUpScreen = () => {
    const [username, setUsername]= useState('');
    const [password, setPassword] = useState('');
    const {height} = useWindowDimensions();
    const onSignInPress = () => {
        console.warn('Connecté');
    }

    const onSignInWithFacebookPress = () => {
      console.warn("Connecté avec Facebook");
    };

    const onSignInWithGooglePress = () => {
      console.warn("Connecté avec Google");
    };

    const onSignInWithApplePress = () => {
      console.warn("Connecté avec Apple");
    };

    const onForgotPassPress = () => {
      console.warn("Forgot");
    };
    const onNewAccountPress = () => {
      console.warn("Création de compte");
    };

    return (
      <ScrollView>
        <View style={styles.root}>
          <Text style={styles.title}>
            Créer un compte
          </Text>
          <CustomInput
            placeholder={"Email"}
            value={username}
            setValue={setUsername}
          />
          <CustomInput
            placeholder={"Mot de Passe"}
            value={password}
            setValue={setPassword}
            secureTextEntry={true}
          />
          <CustomButton onPress={onSignInPress} text={"Se connecter"} />

          <CustomButton
            onPress={onForgotPassPress}
            text={"Mot de passe oublié ?"}
            type="TERTIARY"
          />

          <CustomButton
            onPress={onSignInWithFacebookPress}
            text={"Se connecter avec Facebook"}
            bgColor="#E7EAF4"
            fgColor="#4765A9"
            type="SOCIALCONNECT"
          />
          <CustomButton
            onPress={onSignInWithGooglePress}
            text={"Se connecter avec Google"}
            bgColor="#FAE9EA"
            fgColor="#DD4D44"
            type="SOCIALCONNECT"
          />
          <CustomButton
            onPress={onSignInWithApplePress}
            text={"Se connecter avec Apple"}
            bgColor="#E3E3E3"
            fgColor="#363636"
            type="SOCIALCONNECT"
          />
          <CustomButton
            onPress={onNewAccountPress}
            text={"Pas encore de compte ? Inscrivez-vous"}
            type="TERTIARY"
          />
        </View>
    </ScrollView>
    );
}

export default SignUpScreen

const styles = StyleSheet.create({
  root: {
    padding: 10,
    alignItems: "center",
  },
  logo: {
    width: "100%",
    maxWidth: 250,
    height: "100%",
    maxHeight: 250,
    marginBottom: 50,
  },
  title: {
    textTransform: "capitalize",
    fontSize: 24,
    fontWeight: "bold",
    margin: 10,
  },
});
