import { useState } from "react";

import axios from "axios";
import { DB_PATH } from "../services/env"

export const login = async(email, password, chemin) => {
    var dataLogin = null
    await axios({
        method: "post",
        url: DB_PATH + chemin,
        data: {
            email: email,
            password: password,
        },
        }).then((response) => {
        //Si la response à la requête est acceptée par l'API, l'utilisateur est redirigé vers la page d'accueil.
        // console.warn(state)
        response.status == 201 ? dataLogin = response.data : {}
        console.log(dataLogin)
        return (dataLogin)
            // ? saveToken(
            //     response.data.token
            // ) /* navigation.navigate('Home', {
            //         token : response.data.token,
            //     })  */
            // : {};
    });
}
