import React, { useState } from 'react'
import { View, Text, StyleSheet, ScrollView } from 'react-native'
import CustomInput from '../../components/CustomInput'
import CustomButton from '../../components/CustomButton'
import SocialSignInButtons from '../../components/SocialSignInButtons'

const confirmEmailScreen = () => {
    const [username, setUsername]= useState('');
    const [email, setemail] = useState("");
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState("");

    const onSignUpPress = () => {
        console.warn('Enregistrer');
    }


    const onAlreadyRegisterPress = () => {
      console.warn("Déjà inscrit ? Se connecter");
    };

    const OnPressUsePolicy = () => {
      console.warn("Use Policy");
    }

    const OnPressPrivacyPolicy = () => {
      console.warn("Privacy Policy");
    };

    return (
      <ScrollView>
        <View style={styles.root}>
          <Text style={styles.title}>Confirmer l'email</Text>
          <CustomInput
            placeholder={"Pseudo"}
            value={email}
            setValue={setemail}
          />
          <CustomInput
            placeholder={"Email"}
            value={username}
            setValue={setUsername}
          />
          <CustomInput
            placeholder={"Mot de Passe"}
            value={password}
            setValue={setPassword}
            secureTextEntry={true}
          />
          <CustomInput
            placeholder={"Confirmer le Mot de Passe"}
            value={confirmPassword}
            setValue={setConfirmPassword}
            secureTextEntry={true}
          />
          <CustomButton onPress={onSignUpPress} text={"S'enregistrer"} />

          <Text style={styles.text}>
            En vous enregistrant, vous acceptez la{' '}
            <Text style={styles.link} onPress={OnPressUsePolicy}>politique d'utilisation</Text> ainsi que la{' '}
            <Text style={styles.link} onPress={OnPressPrivacyPolicy}>politique de confidentialité</Text> d'OrgaBud
          </Text>

          <SocialSignInButtons />
          <CustomButton
            onPress={onAlreadyRegisterPress}
            text={"Déjà inscrit ? Connectez-vous"}
            type="TERTIARY"
          />
        </View>
      </ScrollView>
    );
}

export default confirmEmailScreen;

const styles = StyleSheet.create({
  root: {
    padding: 10,
    alignItems: "center",
  },
  title: {
    textTransform: "capitalize",
    fontSize: 24,
    fontWeight: "bold",
    margin: 10,
  },
  text:{
    color: "#fff",
    marginVertical: 10
  },
  link:{
    color: "salmon"
  }
});
