import React, {useState} from 'react'
import { View, Text, StyleSheet, Pressable } from 'react-native'

const CustomButton = ({ onPress, text, type = "PRIMARY", bgColor, fgColor}) => {
  const [clicked, setCliked] = useState(false)
    return (
      <Pressable
        onPress={(onPress, () => setCliked(!clicked))}
        style={[
          styles.container,
          styles[`container_${type}`],
          bgColor ? { backgroundColor: bgColor } : {},
          clicked == true
            ? { boxShadow: "0 5px #666", transform: translateY("4px") }
            : {},
        ]}
      >
        <Text
          style={[
            styles.text,
            styles[`text_${type}`],
            fgColor ? { color: fgColor } : {},
          ]}
        >
          {text}
        </Text>
      </Pressable>
    ); 
}

export default CustomButton

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    color: "white",
    width: "100%",
    borderColor: "#5b712c",
    borderWidth: 1,
    borderRadius: 5,
    padding: 10,
    marginVertical: 5,
  },
  containerActive: {
    // background-color: #3e8e41;
  boxShadow: "0 5px #666",
  transform: translateY("4px")
  },


  container_PRIMARY: {
    backgroundColor: "#5b712c",
  },
  container_SECONDARY: {
    borderColor: "#5b712c",
    borderWidth: 2,
  },

  container_TERTIARY: {
    borderWidth: 0,
  },
  container_CELL: {
    borderWidth: 0,
  },
  container_SOCIALCONNECT: {
    borderWidth: 0,
  },
  container_ADD: {
    backgroundColor: "green",
    borderRadius: 100,
  },
  container_DELETE: {
    borderWidth: 0,
  },
  container_RESUME: {
    borderColor: "#5b712c",
    borderWidth: 2,
    height: 100,
    justifyContent: "center",
  },

  text: {
    fontWeight: "bold",
    color: "white",
    textTransform: "uppercase",
  },
  text_SECONDARY: {
    color: "#0B3B0B",
  },

  text_TERTIARY: {
    fontWeight: "300",
    color: "#5b712c",
    textTransform: "none",
    fontStyle: "italic",
  },
  text_CELL: {
    fontWeight: "500",
    color: "#5b712c",
    textTransform: "none",
    // fontStyle: "italic",
  },
  text_DELETE: {
    fontWeight: "500",
    fontSize: 30,
    color: "red",
    textTransform: "uppercase",
    // fontStyle: "italic",
  },
  text_RESUME: {
    fontSize: 25,
  },
});
