import React, { useState } from "react";
import { View, Text, StyleSheet, ScrollView, Button } from "react-native";
import CustomInput from "../../components/CustomInput";
import CustomButton from "../../components/CustomButton";
import Checkbox from "expo-checkbox";
import DateTimePicker from "@react-native-community/datetimepicker";
import { useNavigation } from "@react-navigation/native";
import { API } from "../../services/env";
import axios from "axios";
import CustomRedirectAlert from "../../components/CustomRedirectAlert";

const NewEntryBudgetScreen = () => {
    const [isChecked, setChecked] = useState(false);
    const [date, setDate] = useState(new Date(1598051730000));
    const [mode, setMode] = useState("date");
    const [show, setShow] = useState(false);

    const onChange = (event, selectedDate) => {
      const currentDate = selectedDate;
      setShow(false);
      setDate(currentDate);
    };

    const showMode = (currentMode) => {
      if (Platform.OS === "android") {
        setShow(false);
        // for iOS, add a button that closes the picker
      }
      setMode(currentMode);
    };

    const showDatepicker = () => {
      showMode("date");
    };

    const showTimepicker = () => {
      showMode("time");
    };
    return (
      <ScrollView>
        <View>
          <Text>Formulaire de nouvelle entrée budget</Text>
          <CustomInput placeholder={"Nom du Champs"} value={""} setValue={""} />
          <CustomInput placeholder={"Montant"} value={""} setValue={""} />
          <CustomInput
            placeholder={"Date de dépense"}
            value={""}
            setValue={""}
          />
          <CustomInput
            placeholder={"Date prévue de débit"}
            value={""}
            setValue={""}
          />

          <Checkbox
            style={""}
            value={isChecked}
            onValueChange={setChecked}
            color={isChecked ? "green" : undefined}
          />
          <Text>Débité</Text>
          <CustomButton onPress={""} text={"Ajouter au budget"} />

          <CustomButton
            onPress={""}
            text={"Retour à la connexion"}
            type="TERTIARY"
          />

          <Button onPress={showDatepicker} title="Show date picker!" />
          <Text>selected: {date.toLocaleString()}</Text>
          {show && (
            <DateTimePicker
              testID="dateTimePicker"
              value={date}
              mode={mode}
              is24Hour={true}
              onChange={onChange}
            />
          )}
        </View>
      </ScrollView>
    );
}

export default NewEntryBudgetScreen

const styles = StyleSheet.create({})
